import a from "/menuData.js";
let menuObj = a,
  imgButton = [...document.querySelectorAll(".menu__container")],
  modal = document.querySelector(".popup-images"),
  button = document.querySelector(".container__button"),
  overlay = document.querySelector("#overlay"),
  imgProduct = document.querySelector(".container__img--image"),
  nameProduct = document.querySelector(".container__text--name"),
  descProduct = document.querySelector(".container__text--description"),
  navbarOptions = document.querySelector(".navbar__options"),
  navbarBtn = document.querySelector(".navbar__logo--hamburguer");
function loadModal(a) {
  let c = a.querySelector(".menu__container--images").src,
    b = a.querySelector(".menu__container--name").innerText;
  (imgProduct.src = c),
    (nameProduct.innerText = b),
    (descProduct.innerText = menuObj[b]);
}
imgButton.forEach((a) => {
  a.addEventListener("click", () => {
    loadModal(a),
      (modal.style.display = "grid"),
      modal.classList.add("active"),
      overlay.classList.add("active");
  });
}),
  button.addEventListener("click", () => {
    modal.classList.remove("active"), overlay.classList.remove("active");
  }),
  overlay.addEventListener("click", () => {
    modal.classList.remove("active"), overlay.classList.remove("active");
  }),
  navbarBtn.addEventListener("click", () => {
    "grid" === navbarOptions.style.display
      ? (navbarOptions.style.display = "none")
      : (navbarOptions.style.display = "grid");
  });
